<?php
// 月末日までループ
for ($i = 1; $i <= $last_day ; $i++)
{
    // 曜日を取得する
    $week = date('w', mktime(0, 0, 0, $month, $i, $year));

    // 一日の場合
    if($i == 1)
    {
        // 一日の曜日までをループ
        for($ii = 1; $ii <= $week; $ii++)
        {
            // 前半に空文字をセットする
            $calender[$j]['day'] = '';
            $j++;

        }
    }

    // 配列に日付をセットする
    $calender[$j]['day'] = $i;
    for($d = 0; $d <= count($search_tasks); $d++)
    {
        // カレンダー上の日付と、DBの日付が一致すればDBデータを入れる
        if($calender[$j]['day'] === $search_tasks[$d]['day'])
        {
            $calender[$j]['opening_hour'] = '出：' . date('G:i', strtotime($search_tasks[$d]['opening_hour']));
            $calender[$j]['ending_hour'] = '退：' . date('G:i', strtotime($search_tasks[$d]['ending_hour']));
        }
    }
    $j++;

    // 月末日の場合
    if($i == $last_day)
    {
        // 月末日から残りをループ
        for($e = 1; $e <= 6 - $week; $e++)
        {
            // 後半に空文字をセット
            $calender [$j]['day'] = '';
            $j++;
        }
    }
}
?>